// Input (Google Play Reviews API) expected at: $input.first().json.reviews : Review[]
// Output (per review):
// {
//   reviewId, author, territory, platform, replyCharLimit, hasResponse,
//   customerTexts: [{ title, text, stars, createdDate }],
//   developerTexts: [{ text, lastModifiedDate, state, responseId }],
//   appVersion, appName
// }

const reviews = $input.first().json.reviews;

// Pull app name from START node
const appName = $item(0).$node["START"].json.client_name || null;

if (!Array.isArray(reviews)) {
  throw new Error('Expected "reviews" to be an array, but got: ' + typeof reviews);
}

function toIsoFromPb(ts) {
  if (!ts) return null;
  const sec = Number(ts.seconds || 0);
  const nanos = Number(ts.nanos || 0);
  const ms = sec * 1000 + Math.floor(nanos / 1e6);
  const d = new Date(ms);
  return isNaN(d.getTime()) ? null : d.toISOString();
}

function toMsFromPb(ts) {
  if (!ts) return 0;
  const sec = Number(ts.seconds || 0);
  const nanos = Number(ts.nanos || 0);
  return sec * 1000 + Math.floor(nanos / 1e6);
}

function clean(s) {
  return (typeof s === 'string' ? s.trim() : '');
}

return reviews.map(review => {
  const reviewId = review.reviewId || null;
  const author = review.authorName || '';
  const comments = Array.isArray(review.comments) ? review.comments : [];

  // Build arrays from all comments (if multiple exist)
  const customerTexts = [];
  const developerTexts = [];

  // Track latest userComment to pick most relevant appVersion
  let latestUserMs = -1;
  let appVersionName = null;

  for (const c of comments) {
    if (c.userComment) {
      const uc = c.userComment;
      const createdIso = toIsoFromPb(uc.lastModified);
      const createdMs = toMsFromPb(uc.lastModified);

      customerTexts.push({
        title: "", // Google Play has no separate title
        text: clean(uc.text || uc.originalText || ""),
        stars: typeof uc.starRating === "number" ? uc.starRating : -1,
        createdDate: createdIso || null,
      });

      if (createdMs > latestUserMs) {
        latestUserMs = createdMs;
        appVersionName = uc.appVersionName || null; // <-- capture version
      }
    } else if (c.developerComment) {
      const dc = c.developerComment;
      developerTexts.push({
        text: clean(dc.text || ""),
        lastModifiedDate: toIsoFromPb(dc.lastModified) || null,
        // Keep same keys as iOS for symmetry
        state: null,
        responseId: null,
      });
    }
  }

  // Sort by date ascending to render as a conversation (optional but nice)
  const parseIso = s => (s ? new Date(s).getTime() : 0);
  customerTexts.sort((a, b) => parseIso(a.createdDate) - parseIso(b.createdDate));
  developerTexts.sort((a, b) => parseIso(a.lastModifiedDate) - parseIso(b.lastModifiedDate));

  return {
    json: {
      reviewId,
      author,
      territory: null,           // not provided by Google Play
      platform: 'Android',
      appName,                   // <-- added
      appVersion: appVersionName,// <-- added
      customerTexts,
      developerTexts,
      replyCharLimit: 350        // Play‚Äôs reply limit
    }
  };
});

// Produces ONLY arrays for the convo, no top-level text/title/content/date.
// Output per review:
// {
//   reviewId, author, territory, platform, appVersion, hasResponse, replyCharLimit,
//   customerTexts: [{ title, text, stars, createdDate }],
//   developerTexts: [{ text, lastModifiedDate, state, responseId }]
// }

// Handles inputs like:
// 1) [{ json: { data:[customerReviews...], included:[customerReviewResponses...] } }]
// 2) [{ json: { type:"customerReviews", ... } }, ...]  (already flattened)

const pages = $input.all();
const out = [];

// Pull app name from START node
const appName = $item(0).$node["START"].json.client_name || null;

// Index all included responses (from any upstream page/item)
const responseIndex = new Map();
for (const item of pages) {
  const inc = item.json?.included;
  if (Array.isArray(inc)) {
    for (const o of inc) {
      if (o?.type === "customerReviewResponses" && o.id) {
        responseIndex.set(o.id, o);
      }
    }
  }
}

const toIso = (s) => {
  if (!s) return null;
  const d = new Date(s);
  return isNaN(d.getTime()) ? s : d.toISOString();
};

function buildCustomerTexts(r) {
  const a = r.attributes || {};
  return [{
    title: a.title || "",
    text: a.body || "",
    stars: typeof a.rating === "number" ? a.rating : -1,
    createdDate: toIso(a.createdDate) || null,
  }];
}

function findDeveloperResponse(r) {
  const relId = r?.relationships?.response?.data?.id;
  if (!relId) return null;
  const resp = responseIndex.get(relId);
  if (!resp) return null;
  const aa = resp.attributes || {};
  return {
    text: aa.responseBody || "",
    lastModifiedDate: toIso(aa.lastModifiedDate) || null,
    state: aa.state || null,           // e.g., "PUBLISHED"
    responseId: resp.id || null,
  };
}

function pushReview(r) {
  const a = r.attributes || {};
  const customerTexts = buildCustomerTexts(r);
  const dev = findDeveloperResponse(r);
  const developerTexts = dev ? [dev] : [];

  out.push({
    json: {
      // minimal non-text metadata
      reviewId: r.id || null,
      author: a.reviewerNickname || "",
      territory: a.territory || null,
      platform: "iOS",
      appVersion: a.appVersionString || null,   // <-- kept
      appName,                                   // <-- added

      // convo arrays ONLY
      customerTexts,
      developerTexts,

      replyCharLimit: 5000,
    },
  });
}

for (const item of pages) {
  const j = item.json || {};
  if (Array.isArray(j.data)) {
    for (const r of j.data) {
      if (r?.type === "customerReviews") pushReview(r);
    }
  } else if (j?.type === "customerReviews") {
    pushReview(j);
  }
  // ignore meta/links-only payloads
}

return out;

// Grab ALL items from "Prepare Versions" run 0, output 0
const versionItems = $items('Prepare Versions', 0, 0); // <-- runIndex fixed at 0
const out = [];

for (let i = 0; i < items.length; i++) {
  const httpItem = items[i];
  const versionString = versionItems[i].json.versionString; // same position
  const reviews = httpItem.json.data || [];

  for (const r of reviews) {
    out.push({
      json: {
        ...r,
        attributes: {
          ...r.attributes,
          appVersionString: versionString,
        },
      },
    });
  }
}

return out;

// Input is the List Versions HTTP node output (items[0])
const versions = (items[0].json.data || [])
  .filter(v => v.attributes.platform === 'IOS'); // keep IOS only

return versions.map(v => ({
  json: {
    versionId: v.id,
    versionString: v.attributes.versionString
  }
}));
